package service;

import java.io.File;
import org.junit.Test;

public class ImageServiceTest {
    @Test
    public void testUploadBase64() {
        String content = "";
        String result = new ImageService().upload(content);
        System.out.println(result);
    }
    
    @Test
    public void testUploadFile() {
        File file = new File("web/img/logo.png");
        String url = new ImageService().upload(file);
        assert url != null;
        System.out.println(url);
    }
}
